import { useState } from 'react';

function Square({value, onSquareClick}) { // component

  // const [value, setValue] = useState(null); // value is equal to null

  // function handleClick() { // function

  //   setValue("X"); // setValue is a function that transform the var value
  //   // console.log('clicked!');
  // }

  return (
    <button 
      className="square"
      onClick = {onSquareClick} // activate this function after click
    >
      {value}
    </button>
  );
}

export default function Board() {

    const [squares, setSquares] = useState(Array(9).fill(null));

    function handleClick() {
      const nextSquares = squares.slice(); // the .slice() allows to copy of the array squares
      nextSquares[0] = "X";
      setSquares(nextSquares);
    }

    return (
      <>
        <div className = "board-row">
          <Square value = {squares[0]} onSquareClick = {handleClick}/>
          <Square value = {squares[1]} onSquareClick = {handleClick}/>
          <Square value = {squares[2]} onSquareClick = {handleClick}/>
        </div>
        <div className = "board-row">
          <Square value = {squares[3]} onSquareClick = {handleClick}/>
          <Square value = {squares[4]} onSquareClick = {handleClick}/>
          <Square value = {squares[5]} onSquareClick = {handleClick}/>
        </div>
        <div className = "board-row">
          <Square value = {squares[6]} onSquareClick = {handleClick}/>
          <Square value = {squares[7]} onSquareClick = {handleClick}/>
          <Square value = {squares[8]} onSquareClick = {handleClick}/>
        </div>
      </>
    );
  }
